-- Business scenario Q136 - Analysing Customer Locations for Sales OptimisationSELECT PA.City	, SUM(SOH.TotalDue) AS TotalSalesAmountFROM Person.Address PAINNER JOIN Person.BusinessEntityAddress BEAON PA.AddressID = BEA.AddressIDINNER JOIN Sales.SalesOrderHeader SOHON BEA.BusinessEntityID = SOH.CustomerIDGROUP BY PA.CityORDER BY SUM(SOH.TotalDue) DESC;-- Business scenario Q137 - Customer Geographical Analysis for Sales ExpansionSELECT *FROM;