-- Business scenario Q141 - Password Complexity AssessmentSELECT PP.BusinessEntityID AS PersonID	, PP.FirstName	, PP.LastName	, PPW.PasswordHashFROM Person.Person PPINNER JOIN Person.Password PPWON PP.BusinessEntityID = PPW.BusinessEntityIDORDER BY PP.BusinessEntityID;-- Business scenario Q142 - Employee and Customer Contact InformationSELECT PP.BusinessEntityID	, PP.FirstName	, PP.LastName	, ISNULL(PPP.PhoneNumber, '') AS PhoneNumberFROM Person.Person PPLEFT JOIN HumanResources.Employee HREON PP.BusinessEntityID = HRE.BusinessEntityIDLEFT JOIN Sales.Customer SCON PP.BusinessEntityID = SC.PersonIDLEFT JOIN Person.EmailAddress EAON PP.BusinessEntityID = EA.BusinessEntityIDLEFT JOIN Person.PersonPhone PPPON PP.BusinessEntityID = PPP.BusinessEntityIDORDER BY PP.BusinessEntityID;-- Business scenario Q143 - Sales Tax Rates by StateSELECT PA.City	, SP.Name AS StateName	, TR.TaxRate AS SalesTaxRateFROM Person.Address PAINNER JOIN Person.StateProvince SPON PA.StateProvinceID = SP.StateProvinceIDINNER JOIN Sales.SalesTaxRate TRON SP.StateProvinceID = TR.StateProvinceIDORDER BY PA.City;-- Business scenario Q144 - Employee Department History TrackingSELECT EDH.DepartmentID	, HE.JobTitle	, HE.NationalIDNumber	, HD.Name AS DepartmentName	, EDH.StartDate	, EDH.EndDateFROM HumanResources.EmployeeDepartmentHistory EDHINNER JOIN HumanResources.Employee HEON EDH.BusinessEntityID = HE.BusinessEntityIDINNER JOIN HumanResources.Department HDON EDH.DepartmentID = HD.DepartmentIDORDER BY EDH.DepartmentID, EDH.StartDate;-- Business scenario Q145 - Job Candidate EvaluationSELECT JC.JobCandidateID	, JC.Resume AS CandidateResume	, HE.JobTitleFROM HumanResources.JobCandidate JCLEFT JOIN HumanResources.Employee HEON JC.BusinessEntityID = HE.BusinessEntityIDWHERE HE.JobTitle IS NOT NULL ;