 -- Business scenario Q11 - Price Existence Check (using EXISTS) SELECT Cost FROM CustomerTab.CustomerProductTabl WHERE EXISTS (SELECT Cost				FROM CustomerTab.CustomerProductTabl				WHERE Cost > 6) ; -- Business scenario Q12 - Price Threshold Check (with ALL)SELECT CostFROM CustomerTab.CustomerProductTablWHERE Cost > ALL (SELECT Cost			FROM CustomerTab.CustomerProductTabl			WHERE Cost < 4);-- Business scenario Q13 - Price Threshold Check (with ANY)SELECT *FROM CustomerTab.CustomerProductTablWHERE 4 < ANY (SELECT Cost				FROM CustomerTab.CustomerProductTabl				);-- Business scenario Q14 - Total Income Threshold Calculation and FilteringSELECT EmployeeID	, JobTitle	, HourlyRate	, (Salary + Bonus) AS TotalIncomeFROM Employee.PayWHERE (Salary + Bonus) > 3000;-- Business scenario Q15 - Hourly Rate Threshold Calculation and FilteringSELECT EmployeeID	, JobTitle	, (HourlyRate * 100) AS HourlyRateThreshFROM Employee.PayWHERE (HourlyRate * 100) > 1500;-- Business scenario Q16 - Price Range Product ListingSELECT *FROM CustomerTab.CustomerProductTablORDER BY Cost ASC;-- . Business scenario Q17 - Expensive Product ListingSELECT *FROM CustomerTab.CustomerProductTablORDER BY Cost DESC;-- Business scenario Q18 - Low-Cost Product ListingSELECT *FROM CustomerTab.CustomerProductTablWHERE Cost < 6ORDER BY Cost ASC;-- Business scenario Q19 - High-Cost Product ListingSELECT *FROM CustomerTab.CustomerProductTablWHERE Cost > 3ORDER BY Cost DESC;-- Business scenario Q20 - Affordable Products with Alphabetical OrderSELECT *FROM CustomerTab.CustomerProductTablWHERE Cost < 5ORDER BY ProductDescription;