 -- Business scenario Q11 - Price Existence Check (using EXISTS) SELECT Cost FROM CustomerTab.CustomerProductTabl WHERE EXISTS (SELECT Cost				FROM CustomerTab.CustomerProductTabl				WHERE Cost > 6) ; -- Business scenario Q12 - Price Threshold Check (with ALL)SELECT CostFROM CustomerTab.CustomerProductTablWHERE Cost > ALL (SELECT Cost			FROM CustomerTab.CustomerProductTabl			WHERE Cost < 4);