-- Business scenario Q151 - Tracking Work Order ProgressUSE AdventureWorks2022SELECT WO.WorkOrderID	, WO.ProductID	, PL.LocationID	, PL.Name AS LocationName	, WOR.ScheduledStartDate	, WOR.ActualStartDate	, WOR.ScheduledEndDate	, WOR.ActualEndDate	, WOR.ActualResourceHrsFROM Production.WorkOrder WOINNER JOIN Production.WorkOrderRouting WORON WO.WorkOrderID = WOR.WorkOrderIDINNER JOIN Production.Location PLON WOR.LocationID = PL.LocationID;-- Business scenario Q152 - Tracking Sales Quota HistorySELECT SP.BusinessEntityID AS SalesPersonID	, PQH.QuotaDate	, PQH.SalesQuotaFROM Sales.SalesPersonQuotaHistory PQHINNER JOIN Sales.SalesPerson SPON PQH.BusinessEntityID = SP.BusinessEntityIDORDER BY SP.BusinessEntityID, PQH.QuotaDate;-- Business scenario Q153 - Identifying Popular ProductsSELECT PP.ProductID	, PP.Name AS ProductName	, PP.ProductNumber	, COUNT(CI.ShoppingCartItemID) AS TimesAddedToCartFROM Production.Product PPLEFT JOIN Sales.ShoppingCartItem CION PP.ProductID = CI.ProductIDGROUP BY PP.ProductID, PP.Name, PP.ProductNumberORDER BY COUNT(CI.ShoppingCartItemID) DESC;-- Business scenario Q154 - Optimising Cross-Selling in a Banking EnvironmentUSE [Finance and Banking]SELECT UC.CustomerID	, CONCAT(UC.FirstName, ' ', UC.LastName) AS FullName	, UC.Email	, UC.PhoneNumber	, UA.AccountID	, UA.AccountType	, UA.BalanceFROM Finance.UniqueCustomers UCINNER JOIN Finance.UniqueAccounts UAON UC.CustomerID = UA.CustomerIDWHERE UA.AccountType = 'Savings' AND UA.Balance > 5000ORDER BY UA.Balance;-- Business scenario Q155 - Fraud Detection and Prevention in BankingSELECT UC.CustomerID	, CONCAT(UC.FirstName, ' ', UC.LastName) AS FullName	, UC.Email	, UC.PhoneNumber	, UA.AccountID	, UA.AccountType	, UA.Balance	, UT.TransactionDate	, UT.AmountFROM Finance.UniqueCustomers UCINNER JOIN Finance.UniqueAccounts UAON UC.CustomerID = UA.CustomerIDINNER JOIN Finance.UniqueTransactions UTON UA.AccountID = UT.AccountIDWHERE (UA.Balance < 0) OR (UT.Amount < 1000) OR (UT.Amount > 5000);